// Filter items: keep only those newer than 20 minutes
const cutoff = Date.now() - 20 * 60  * 60 * 1000;

// In Code-Node v2 benutzen wir $input.all() um alle Items zu holen
const inputItems = $input.all();
const out = [];

for (const item of inputItems) {
  const j = item.json;

  // M√∂gliche Datumsfelder aus dem RSS
  const candidates = [j.isoDate, j.pubDate, j.date, j.pubdate];

  let pubDate = null;
  for (const c of candidates) {
    if (!c) continue;
    const d = new Date(c);
    if (!isNaN(d.getTime())) {
      pubDate = d;
      break;
    }
  }

  // Falls kein Datum gefunden ‚Üí (f√ºr Tests) durchlassen
