// Code node v2
const input = $input.all();

function stripHtml(s=''){
  return s.replace(/<[^>]*>/g,'').replace(/\s+/g,' ').trim();
}
function firstParagraphFromHtml(html=''){
  // Falls <p>...</p> existiert, den ersten nehmen
  const m = html.match(/<p[^>]*>([\s\S]*?)<\/p>/i);
  if (m) return stripHtml(m[1]);
  // Fallback: nach Leerzeilen splitten
  const text = stripHtml(html);
  return text.split(/(?:\n\s*\n)|(?:\r?\n){2,}/)[0] || text;
}

return input.map(item => {
  const j = item.json || {};
  let image = j.enclosure?.url
           || j['media:content']?.url
           || j['media:thumbnail']?.url
           || j['media:group']?.['media:content']?.url
           || '';

  const html = (j.content || j.summary || j.description || '');
  const teaser = typeof html === 'string' ? firstParagraphFromHtml(html) : '';

  item.json.imageUrl  = image || '';
  item.json.teaser_de = teaser || '';

  return item;
});

// Code node v2  (Build Telegram payload)
const items = $input.all();

const esc = (s='') =>
  s.replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;');

function hostFrom(link=''){
  const m = link.match(/^https?:\/\/([^\/?#]+)/i);
  return m ? m[1].replace(/^www\./,'') : '';
}

// sanftes Trimmen von Plaintext (vor HTML-Escaping), um Wortgrenzen zu respektieren
function truncatePlain(s = '', max = 700) {
  if (s.length <= max) return s;
  const cut = s.slice(0, max);
  const back = cut.lastIndexOf(' ');
  return (back > 40 ? cut.slice(0, back) : cut) + '‚Ä¶';
}

// Caption-Limit f√ºr Telegram sicher handhaben, inkl. RTL-PDF am Ende
function capRTL(s, max = 1024, PDF = '\u202C') {
  if (s.length <= max) return s;
  // Falls am Ende bereits ein PDF steht, kurz entfernen, dann wieder anh√§ngen
  const withoutPDF = s.replace(/\u202C$/, '');
  const cut = withoutPDF.slice(0, max - 1); // Platz f√ºr Ellipsis lassen
  return cut + '‚Ä¶' + PDF;
}

// RTL/LTR Steuerzeichen
const RLE = '\u202B'; // Right-to-Left Embedding
const PDF = '\u202C'; // Pop Directional Formatting
const LRI = '\u2066'; // Left-to-Right Isolate
const PDI = '\u2069'; // Pop Directional Isolate

// Optional: sehr lockere Foto-Validierung (nur wenn du willst)
// const isValidPhoto = (u='') => /^https?:\/\//i.test(u) && /(jpg|jpeg|png|gif)(\?|$)/i.test(u);

return items.map(i => {
  const j = i.json || {};

  const titleFa  = (j.title_fa  || j.title || '').toString().trim();
  const teaserFa = (j.teaser_fa || '').toString().trim();
  const link     = (j.link || j.guid || '').toString().trim();
  let   photo    = (j.imageUrl || '').toString().trim();
  // if (!isValidPhoto(photo)) photo = '';

  // Teaser vorab (plaintext) begrenzen, damit wir sp√§ter HTML nicht zerrei√üen
  const teaserTrimmed = truncatePlain(teaserFa, 700);

  // Kern der Caption (noch ohne globale RTL-H√ºlle)
  let captionCore = `üì∞ <b>${esc(titleFa)}</b>`;
  if (teaserTrimmed) captionCore += `\n\n\n${esc(teaserTrimmed)}`;

  if (link) {
    const host = hostFrom(link) || 'ŸÑ€åŸÜ⁄© ÿÆÿ®ÿ±';
    // Host/Link LTR-kapseln, damit RTL-Layout stabil bleibt
    const hostHtml = `${LRI}<a href="${esc(link)}">${esc(host)}</a>${PDI}`;
    captionCore += `\n\nüîó ${hostHtml}`;
  }

  // Kanalhandle (lateinisch) ebenfalls LTR isolieren
  const channelHandle = `${LRI}@FootbllSpiegelFa${PDI}`;
  captionCore += `\n\n${channelHandle}`;

  // Ganze Caption in RTL einbetten
  let caption = `${RLE}${captionCore}${PDF}`;

  // Fallback-Text = identisch
  let text = caption;

  // RTL-sicher k√ºrzen (Ellipsis + PDF ans Ende)
  caption = capRTL(caption, 1024, PDF);
  text    = capRTL(text,    4096, PDF); // Text kann l√§nger sein; 4096 reicht komfortabel

  return {
    json: {
      ...j,
      telegram: {
        photo,
        caption,
        text,
        replyMarkup: link
          ? { inline_keyboard: [[{ text: 'ŸÑ€åŸÜ⁄© ÿÆÿ®ÿ± üîó', url: link }]] }
          : undefined
      }
    }
  };
});

// Normalize + de-duplicate + sort items from multiple RSS feeds
// Output fields: sourceHost, sourceFeed, title, link, isoDate, pubDate, content, imageUrl

/** @type {Array<{json: any}>} */
const items = $input.all();

/**
 * @param {string} [link]
 */
function hostFrom(link = '') {
  const m = (link || '').match(/^https?:\/\/([^\/?#]+)/i);
  return m ? m[1].replace(/^www\./, '') : '';
}

/**
 * Robust image picker across common RSS shapes
 * @param {Record<string, any>} [j]
 * @returns {string}
 */
function pickImage(j = {}) {
  // media:content can be an object or an array
  const media = j['media:content'];
  if (media) {
    if (Array.isArray(media)) {
      const m = media.find(x => x && x.url);
      if (m?.url) return String(m.url);
    } else if (typeof media === 'object' && media.url) {
      return String(media.url);
    }
  }

  const enc = j.enclosure;
  if (enc && typeof enc === 'object' && enc.url) return String(enc.url);

  const img = j.image;
  if (img && typeof img === 'object' && img.url) return String(img.url);

  if (j.thumbnail?.url) return String(j.thumbnail.url);

  // manche Feeds packen Bilder in content:encoded als <img ...>
  if (typeof j.content === 'string') {
    const m = j.content.match(/<img[^>]+src=["']([^"']+)["']/i);
    if (m && m[1]) return m[1];
  }

  return '';
}

const seen = new Set();
let out = [];

/** @param {any} v */ const toStr = (v) => (v ?? '').toString().trim();

for (const it of items) {
  const j = it.json ?? {};
  const link = toStr(j.link || j.guid);
  if (!link || seen.has(link)) continue;
  seen.add(link);

  // date normalization
  const rawDate = j.isoDate || j.pubDate || j.date || null;
  let iso = null, pubDate = null;
  if (rawDate) {
    const ts = Date.parse(rawDate);
    if (!Number.isNaN(ts)) {
      const d = new Date(ts);
      iso = d.toISOString();
      pubDate = d.toUTCString();
    }
  }

  out.push({
    json: {
      sourceHost: hostFrom(link) || hostFrom(j.feedUrl || ''),
      sourceFeed: j.feedTitle || j.meta?.title || j.source || '',
      title: toStr(j.title),
      link,
      isoDate: iso,
      pubDate: pubDate,
      content: toStr(j.contentSnippet || j.summary || j.content),
      imageUrl: pickImage(j),
    },
  });
}

// sort desc by isoDate (nulls last)
out.sort((a, b) => {
  const ta = a.json.isoDate ? Date.parse(a.json.isoDate) : 0;
  const tb = b.json.isoDate ? Date.parse(b.json.isoDate) : 0;
  return tb - ta;
});

// optional limit
const limit = 100;
if (out.length > limit) out = out.slice(0, limit);

return out;
